mapper.select:
SELECT user_name, password
FROM application_user
WHERE user_name = #{userName}

SELECT user_name
FROM application_user
WHERE user_name = #{username}

SELECT name
FROM application_user
WHERE user_name = #{username}

SELECT password
FROM application_user
WHERE user_name = #{username};

mapper.update:
UPDATE application_user
SET
    password = #{user.newPassword}
WHERE user_name = #{username}
